import React, { useState } from "react";
import PropTypes from 'prop-types';
import './NewsItem.css'; // We'll create this CSS file

const Newsitem = (props) => {
    const { title, desc, imageurl, newsurl, author, time, source, mode } = props;
    
    return (
        <div className={`news-item ${mode === 'dark' ? 'dark-mode' : ''}`}>
            <div className="news-item-source">
                <span className="badge">{source}</span>
            </div>
            <img 
                src={imageurl} 
                className="news-item-image" 
                alt={title || "News image"} 
                onError={(e) => {
                    e.target.src = "https://www.hindustantimes.com/ht-img/img/2024/06/10/550x309/US-HUNGER-BIDEN-GUN-TRIAL-CONTINUES-IN-DELAWARE-10_1718034063663_1718034099459.jpg";
                }}
            />
            <div className="news-item-content">
                <h3 className="news-item-title">{title ? `${title.slice(0, 50)}...` : "No title available"}</h3>
                <p className="news-item-desc">{desc ? `${desc.slice(0, 120)}...` : "No description available"}</p>
                <div className="news-item-meta">
                    <p>By {author || "unknown"}</p>
                    <p>Last updated: {time ? new Date(time).toGMTString() : 'unknown'}</p>
                </div>
                <a 
                    href={newsurl} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className={`news-item-button ${mode === 'dark' ? 'dark-button' : ''}`}
                >
                    Read More
                </a>
            </div>
        </div>
    );
};

Newsitem.propTypes = {
    title: PropTypes.string,
    desc: PropTypes.string,
    imageurl: PropTypes.string,
    newsurl: PropTypes.string.isRequired,
    author: PropTypes.string,
    time: PropTypes.string,
    source: PropTypes.string,
    mode: PropTypes.string
};

export default Newsitem;
