import React, { useState, useEffect } from 'react';
import Newsitem from './Newsitem';
import Spinner from './Spinner.js';
import InfiniteScroll from 'react-infinite-scroll-component';
import PropTypes from 'prop-types';

const News = (props) => {
  const [articles, setArticles] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [totalResults, setTotalResults] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [error, setError] = useState(null);

  console.log('News component props:', props);
  console.log('Current state:', { articles, page, loading, totalResults, hasMore, error });

  const capitalize = (str) => {
    return str.charAt(0).toUpperCase() + str.slice(1);
  };

  const updateNews = async () => {
    try {
      console.log('Starting to fetch news...');
      props.setProgress(10);
      setLoading(true);
      setError(null);
      
      document.title = `${capitalize(props.category)}-NewsPulse`;
      const url = `http://api.mediastack.com/v1/news?access_key=${props.apikey}&keywords=${props.category}&countries=${props.country}`;
      
      console.log('Fetching from URL:', url);
      
      const response = await fetch(url);
      
      props.setProgress(30);
      
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP error! status: ${response.status}, ${errorText}`);
      }
      
      const data = await response.json();
      console.log('API response data:', data);
      
      props.setProgress(70);
      
      if (data.error) {
        throw new Error(data.error.message || 'Unknown API error');
      }
      
      if (data.data && data.data.length > 0) {
        console.log(`Received ${data.data.length} articles`);
        setArticles(prevArticles => [...prevArticles, ...data.data]);
        setTotalResults(data.pagination?.total || 0);
        setHasMore(page * props.pagesize < (data.pagination?.total || 0));
      } else {
        console.log('No articles received');
        setHasMore(false);
      }
      
      props.setProgress(100);
    } catch (error) {
      console.error("Error fetching news:", error);
      setError(error.message);
      setHasMore(false);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    console.log('Page changed or component mounted, updating news...');
    updateNews();
    // Cleanup function
    return () => {
      console.log('News component unmounting or dependencies changing');
    };
  }, [page, props.country, props.category]);

  const fetchMoreData = () => {
    if (!hasMore) {
      console.log('No more data to fetch');
      return;
    }
    console.log('Fetching more data, incrementing page from', page, 'to', page + 1);
    setPage(prevPage => prevPage + 1);
  };

  return (
    <div className={`news-container ${props.mode === 'dark' ? 'dark-mode' : 'light-mode'}`}>
      <h2 className={`news-heading ${props.mode === 'dark' ? 'text-white' : 'text-dark'}`}>
        NewsPulse - Top {capitalize(props.category)} Headlines
      </h2>
      
      {error && (
        <div className="error-message">
          Error loading news: {error}
        </div>
      )}
      
      {loading && page === 1 && <Spinner mode={props.mode} />}
      
      <div className="news-grid">
        <InfiniteScroll
          dataLength={articles.length}
          next={fetchMoreData}
          hasMore={hasMore}
          loader={<Spinner mode={props.mode} />}
          endMessage={
            <p style={{ textAlign: 'center' }}>
              <b>No more news to show</b>
            </p>
          }
        >
          <div className="news-items">
            {articles.map((element, index) => (
              <div className="news-item-wrapper" key={`${element.url}-${index}`}>
                <Newsitem
                  mode={props.mode}
                  title={element.title ? element.title.slice(0, 50) : ""}
                  desc={element.description ? element.description.slice(0, 120) : ""}
                  imageurl={element.image || "https://www.hindustantimes.com/ht-img/img/2024/06/10/550x309/US-HUNTER-BIDEN-GUN-TRIAL-CONTINUES-IN-DELAWARE-10_1718034063663_1718034099459.jpg"}
                  newsurl={element.url}
                  author={element.author || "unknown"}
                  time={element.publishedAt || 'unknown'}
                  source={element.source || "Unknown source"}
                />
              </div>
            ))}
          </div>
        </InfiniteScroll>
      </div>
    </div>
  );
};

News.defaultProps = {
  pagesize: 10,
  country: 'us',
  category: 'general',
  totalResults: 0
};

News.propTypes = {
  country: PropTypes.string,
  pagesize: PropTypes.number,
  category: PropTypes.string,
  apikey: PropTypes.string.isRequired,
  mode: PropTypes.string,
  setProgress: PropTypes.func
};

export default News;
