import React, { useState, useEffect } from 'react';
import Newsitem from './Newsitem';
import Spinner from './Spinner.js';
import InfiniteScroll from 'react-infinite-scroll-component';
import PropTypes from 'prop-types';
import './News.css'; // We'll create this CSS file

const News = (props) => {
  const [articles, setArticles] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(true);
  const [totalResults, setTotalResults] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [error, setError] = useState(null);

  const capitalize = (str) => {
    return str.charAt(0).toUpperCase() + str.slice(1);
  };

  const updateNews = async () => {
    try {
      props.setProgress(10);
      setLoading(true);
      setError(null);
      
      document.title = `${capitalize(props.category)} - NewsPulse`;
      const url = `http://api.mediastack.com/v1/news?access_key=${props.apikey}&keywords=${props.category}&countries=${props.country}&limit=${props.pagesize}&offset=${(page - 1) * props.pagesize}`;
      
      const response = await fetch(url);
      props.setProgress(30);
      
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP error! status: ${response.status}, ${errorText}`);
      }
      
      const data = await response.json();
      props.setProgress(70);
      
      if (data.error) {
        throw new Error(data.error.message || 'Unknown API error');
      }
      
      if (data.data && data.data.length > 0) {
        setArticles(prevArticles => 
          page === 1 ? data.data : [...prevArticles, ...data.data]
        );
        setTotalResults(data.pagination?.total || 0);
        setHasMore(page * props.pagesize < (data.pagination?.total || 0));
      } else {
        setHasMore(false);
        if (page === 1) setArticles([]);
      }
      
      props.setProgress(100);
    } catch (error) {
      console.error("Error fetching news:", error);
      setError(error.message);
      setHasMore(false);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    updateNews();
  }, [page, props.country, props.category]);

  const fetchMoreData = () => {
    if (!loading && hasMore) {
      setPage(prevPage => prevPage + 1);
    }
  };

  return (
    <div className={`news-container ${props.mode === 'dark' ? 'dark-mode' : ''}`}>
      <h1 className={`news-heading ${props.mode === 'dark' ? 'text-white' : 'text-dark'}`}>
        NewsPulse - Top {capitalize(props.category)} Headlines
      </h1>
      
      {error && (
        <div className={`error-message ${props.mode === 'dark' ? 'text-white' : 'text-danger'}`}>
          Error loading news: {error}
        </div>
      )}
      
      <InfiniteScroll
        dataLength={articles.length}
        next={fetchMoreData}
        hasMore={hasMore}
        loader={loading && <Spinner mode={props.mode} />}
        endMessage={
          <p className={`text-center ${props.mode === 'dark' ? 'text-white' : 'text-dark'}`}>
            <b>You've reached the end of the news</b>
          </p>
        }
        style={{ overflow: 'visible' }}
      >
        <div className="news-grid">
          {articles.map((element, index) => (
            <Newsitem
              key={`${element.url}-${index}`}
              mode={props.mode}
              title={element.title ? element.title.slice(0, 50) : ""}
              desc={element.description ? element.description.slice(0, 120) : ""}
              imageurl={element.image || "https://www.hindustantimes.com/ht-img/img/2024/06/10/550x309/US-HUNGER-BIDEN-GUN-TRIAL-CONTINUES-IN-DELAWARE-10_1718034063663_1718034099459.jpg"}
              newsurl={element.url}
              author={element.author || "unknown"}
              time={element.publishedAt || 'unknown'}
              source={element.source || "Unknown source"}
            />
          ))}
        </div>
      </InfiniteScroll>
    </div>
  );
};

News.defaultProps = {
  pagesize: 10,
  country: 'us',
  category: 'general',
  totalResults: 0
};

News.propTypes = {
  country: PropTypes.string,
  pagesize: PropTypes.number,
  category: PropTypes.string,
  apikey: PropTypes.string.isRequired,
  mode: PropTypes.string,
  setProgress: PropTypes.func
};

export default News;
